import React from "react";
import Accordion, { AccordionProps } from "@mui/material/Accordion";

type SCFAccordionProps = AccordionProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFAccordion = ({show = true, ...muiprops}: SCFAccordionProps) => {
  return show === true ? <Accordion {...muiprops} /> : <></>;
};

export default SCFAccordion;
