import React from 'react';
import Alert, {AlertProps } from '@mui/material/Alert';


type SCFAlertProps = AlertProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFAlert= ({show = true, ...muiprops}: SCFAlertProps) => {
    return show  ? <Alert {...muiprops} /> : <></>;
};

export default SCFAlert;