import React from "react";
import AppBar, { AppBarProps } from "@mui/material/AppBar";

type SCFAppBarProps = AppBarProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFAppBar = ({show = true, ...muiprops}: SCFAppBarProps) => {
  return show === true ? <AppBar {...muiprops} /> : <></>;
};

export default SCFAppBar;