import React from "react";
import Avatar, { AvatarProps } from "@mui/material/Avatar";

type SCFAvatarProps = AvatarProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFAvatar = ({show = true, ...muiprops}: SCFAvatarProps) => {
  return show === true ? <Avatar {...muiprops} /> : <></>;
};

export default SCFAvatar;