import React from "react";
import Badge, { BadgeProps } from "@mui/material/Badge";

type SCFBadgeProps = BadgeProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFBadge = ({show = true, ...muiprops}: SCFBadgeProps) => {
  return show === true ? <Badge {...muiprops} /> : <></>;
};

export default SCFBadge;