import React from "react";
import Menu, { MenuProps } from "@mui/material/Menu";

type SCFBasicMenuProps = MenuProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFBasicMenu= ({show = true, ...muiprops}: SCFBasicMenuProps) => {
  return show === true ? <Menu {...muiprops} /> : <></>;
};

export default SCFBasicMenu;