import React from "react";
import Pagination, { PaginationProps } from "@mui/material/Pagination";

type SCFBasicPaginationProps = PaginationProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFBasicPagination = ({show = true, ...muiprops}: SCFBasicPaginationProps) => {
  return show === true ? <Pagination {...muiprops} /> : <></>;
};

export default SCFBasicPagination;