import React from "react";
import BottomNavigation, { BottomNavigationProps } from "@mui/material/BottomNavigation";

type SCFBottomNavigationProps = BottomNavigationProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFBottomNavigation = ({show = true, ...muiprops}: SCFBottomNavigationProps) => {
  return show === true ? <BottomNavigation {...muiprops} /> : <></>;
};

export default SCFBottomNavigation;