import React from "react";
import Breadcrumbs, { BreadcrumbsProps } from "@mui/material/Breadcrumbs";
type SCFBreadcrumbsProps = BreadcrumbsProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFBreadcrumbs = ({ show = true, ...muiprops }: SCFBreadcrumbsProps) => {
  return show === true ? <Breadcrumbs {...muiprops} /> : <></>;
};

export default SCFBreadcrumbs;
