import React from "react";
import Button, { ButtonProps } from "@mui/material/Button";

type SCFButtonProps = ButtonProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFButton = ({show = true, ...muiprops}: SCFButtonProps) => {
  return show === true ? <Button {...muiprops} /> : <></>;
};

export default SCFButton;
