import React from "react";
import Checkbox, { CheckboxProps } from "@mui/material/Checkbox";

type SCFCheckboxProps = CheckboxProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFCheckbox = ({show = true, ...muiprops}: SCFCheckboxProps) => {
  return show === true ? <Checkbox {...muiprops} /> : <></>;
};

export default SCFCheckbox;