import React from "react";
import Chip, { ChipProps } from "@mui/material/Chip";

type SCFChipProps = ChipProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFChip = ({show = true, ...muiprops}: SCFChipProps) => {
  return show === true ? <Chip {...muiprops} /> : <></>;
};

export default SCFChip;