import React from "react";
import ClickAwayListener, { ClickAwayListenerProps } from "@mui/material/ClickAwayListener";

type SCFClickAwayListenerProps = ClickAwayListenerProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFClickAwayListener= ({show = true, ...muiprops}: SCFClickAwayListenerProps) => {
  return show === true ? <ClickAwayListener {...muiprops} /> : <></>;
};

export default SCFClickAwayListener;