import React from "react";
import Divider, { DividerProps } from "@mui/material/Divider";

type SCFDividerProps = DividerProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFDivider= ({show = true, ...muiprops}: SCFDividerProps) => {
  return show === true ? <Divider {...muiprops} /> : <></>;
};

export default SCFDivider;