import React from "react";
import Stepper, { StepperProps } from "@mui/material/Stepper";

type SCFHorizontalStepperProps = StepperProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFHorizontalStepper= ({show = true, ...muiprops}: SCFHorizontalStepperProps) => {
  return show === true ? <Stepper {...muiprops} /> : <></>;
};

export default SCFHorizontalStepper;