import React from "react";

type SCFIconProps = {
  /**
   * icon css from https://fontawesome.com/.
   */
  icon: string;
  
  /**
   * top div css.
   */
  className?: string;
};

/**
 * @deprecated Use SCFIconButton instead
 */
