import React from "react";
import Link, { LinkProps } from "@mui/material/Link";

type SCFLinkProps = LinkProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFLinks = ({show = true, ...muiprops}: SCFLinkProps) => {
  return show === true ? <Link {...muiprops} /> : <></>;
};

export default SCFLinks;