import React from "react";
import List, { ListProps } from "@mui/material/List";

type SCFListProps = ListProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFList= ({show = true, ...muiprops}: SCFListProps) => {
  return show === true ? <List {...muiprops} /> : <></>;
};

export default SCFList;