import React from "react";
import SCFDialog, { SCFDialogProps } from "../SCFDialog/SCFDialog";

interface SCFModalProps extends SCFDialogProps {
    modalTitle?: React.ReactNode | JSX.Element;
    modalDescription?: React.ReactNode | JSX.Element;
    body?: React.ReactNode | JSX.Element;
    handleModalClose?: () => void;
};

/**
 * @deprecated Use SCFDialog instead
 */
const SCFModal = ({
    show = true, 
    modalTitle = undefined,
    modalDescription = undefined,
    body = undefined,

    handleModalClose = () => {

    },
    ...muiprops
}: SCFModalProps) => {
  return (
      <SCFDialog
        open = {muiprops.open}
        onClose= {muiprops.onClose}
        title = {modalTitle}
        content = {body? body : modalDescription}
      >
      </SCFDialog>
  )
};

export default SCFModal;
