import React from 'react';
import NoSsr, {NoSsrProps } from '@mui/material/NoSsr';


type SCFNoSsrProps = NoSsrProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFNoSsr= ({show = true, ...muiprops}: SCFNoSsrProps) => {
    return show  ? <NoSsr {...muiprops} /> : <></>;
};

export default SCFNoSsr;