import React from 'react';
import Paper, {PaperProps } from '@mui/material/Paper';


type SCFPaperProps = PaperProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFPaper = ({show = true, ...muiprops}: SCFPaperProps) => {
    return show  ? <Paper {...muiprops} /> : <></>;
};

export default SCFPaper;