import React, { useState } from 'react';
import TextField, { TextFieldProps } from '@mui/material/TextField';
import { IconButton, InputAdornment } from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';



type SCFPasswordProps = TextFieldProps & {
    /**
    * Controls if the component must be rendered
    */
    show?: boolean;
    newPasswordValidation?: any;
};
const SCFPassword= ({show = true, newPasswordValidation,  ...propsMaterial}: SCFPasswordProps) => {
    const [showPassword, setShowPassword] = useState(false);
    return (
        <>
            { show && (
                <TextField 
                {...propsMaterial}  
                {...newPasswordValidation}
                type= {showPassword ? 'text' : 'password'}
                InputProps={{
                    endAdornment: (
                        <InputAdornment position="end">
                           <IconButton onClick={() => setShowPassword(!showPassword)}>{showPassword? (<VisibilityIcon />):(<VisibilityOffIcon />)}</IconButton>
                        </InputAdornment>                       
                    )
                }}
                /> 
            )}
        </>
    );
};


export default SCFPassword;