import React from 'react';
import Popover, {PopoverProps } from '@mui/material/Popover';


type SCFPopoverProps = PopoverProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFPopover= ({show = true, ...muiprops}: SCFPopoverProps) => {
    return show  ? <Popover {...muiprops} /> : <></>;
};

export default SCFPopover;