import React from 'react';
import Portal, {PortalProps } from '@mui/material/Portal';


type SCFPortalProps = PortalProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFPortal= ({show = true, ...muiprops}: SCFPortalProps) => {
    return show  ? <Portal {...muiprops} /> : <></>;
};

export default SCFPortal;