import React from 'react';
import Radio, {RadioProps } from '@mui/material/Radio';


type SCFRadioProps = RadioProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFRadio= ({show = true, ...muiprops}: SCFRadioProps) => {
    return show  ? <Radio {...muiprops} /> : <></>;
};

export default SCFRadio;