import React from 'react';
import RadioGroup, {RadioGroupProps } from '@mui/material/RadioGroup';


type SCFRadioGroupProps = RadioGroupProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFRadioGroup= ({show = true, ...muiprops}: SCFRadioGroupProps) => {
    return show  ? <RadioGroup {...muiprops} /> : <></>;
};

export default SCFRadioGroup;