import React from 'react';
import Skeleton, {SkeletonProps } from '@mui/material/Skeleton';


type SCFSkeletonProps = SkeletonProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFSkeleton= ({show = true, ...muiprops}: SCFSkeletonProps) => {
    return show  ? <Skeleton {...muiprops} /> : <></>;
};

export default SCFSkeleton;