import React from 'react';
import Slider, {SliderProps } from '@mui/material/Slider';


type SCFSliderProps = SliderProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFSlider= ({show = true, ...muiprops}: SCFSliderProps) => {
    return show === true ? <Slider {...muiprops} /> : <></>;
};

export default SCFSlider;