import React from 'react';
import Snackbar, {SnackbarProps } from '@mui/material/Snackbar';


type SCFSnackbarProps = SnackbarProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFSnackbar= ({show = true, ...muiprops}: SCFSnackbarProps) => {
    return show === true ? <Snackbar {...muiprops} /> : <></>;
};

export default SCFSnackbar;