import React from 'react';
import CircularProgress, {CircularProgressProps } from '@mui/material/CircularProgress';


type SCFSpinnerProps = CircularProgressProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFSpinner= ({show = true, ...muiprops}: SCFSpinnerProps) => {
    return show === true ? <CircularProgress {...muiprops} /> : <></>;
};

export default SCFSpinner;