import React from 'react';
import Switch, {SwitchProps } from '@mui/material/Switch';


type SCFSwitchProps = SwitchProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFSwitch= ({show = true, ...muiprops}: SCFSwitchProps) => {
    return show === true ? <Switch {...muiprops} /> : <></>;
};

export default SCFSwitch;