import React from 'react';
import Drawer, {DrawerProps } from '@mui/material/Drawer';


type SCFTemporaryDrawerProps = DrawerProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFTemporaryDrawer= ({show = true, ...muiprops}: SCFTemporaryDrawerProps) => {
    return show === true ? <Drawer {...muiprops} /> : <></>;
};

export default SCFTemporaryDrawer;