import React from 'react';
import TextareaAutosize, {TextareaAutosizeProps } from '@mui/material/TextareaAutosize';


type SCFTextareaAutosizeProps = TextareaAutosizeProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFTextareaAutosize= ({show = true, ...muiprops}: SCFTextareaAutosizeProps) => {
    return show === true ? <TextareaAutosize {...muiprops} /> : <></>;
};

export default SCFTextareaAutosize;