import React from 'react';
import TextField, {TextFieldProps } from '@mui/material/TextField';


type SCFTextFieldProps = TextFieldProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
 validate?: any;
};
const SCFTextField= ({show = true, validate, ...muiprops}: SCFTextFieldProps) => {
    return show  ? <TextField {...muiprops} {...validate} /> : <></>;
};

export default SCFTextField;