import * as React from 'react';

import ToggleButton, {ToggleButtonProps} from '@mui/material/ToggleButton';

type SCFToggleButtonProps = ToggleButtonProps & {
    /**
    * Controls if the component must be rendered
    */
    show?: boolean;
};

const SCFToggleButton= ({show = true, ...muiprops}: SCFToggleButtonProps) => {
    return show === true ? <ToggleButton {...muiprops} /> : <></>;
};
        
export default SCFToggleButton;