import React from 'react';
import Tooltip, {TooltipProps } from '@mui/material/Tooltip';


type SCFTooltipProps = TooltipProps & {
/**
* Controls if the component must be rendered
 */
 show?: boolean;
};
const SCFTooltip= ({show = true, ...muiprops}: SCFTooltipProps) => {
    return show === true ? <Tooltip {...muiprops} /> : <></>;
};

export default SCFTooltip;