import React from "react";
import TreeView, { TreeViewProps } from "@mui/lab/TreeView";

type SCFTreeViewProps = TreeViewProps & {
  /**
   * Controls if the component must be rendered
   */
  show?: boolean;
};

const SCFTreeView = ({show = true, ...muiprops}: SCFTreeViewProps) => {
  return show === true ? <TreeView {...muiprops} /> : <></>;
};

export default SCFTreeView;