import React, { useState } from "react";

import Typography from "@mui/material/Typography";

import { SCFBasicMenu, SCFButton, SCFDivider } from "../../components";
import { MenuItem } from "@mui/material";

const SCFBasicMenuDemo = () => {
   const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
   const openBasicMenu = Boolean(anchorEl);
   const handleClickBasicMenu = (event: React.MouseEvent<HTMLButtonElement>) => {
     setAnchorEl(event.currentTarget);
   };
   const handleCloseBasicMenu = () => {
     setAnchorEl(null);
   };
 
  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Basic Menu
      </Typography>
      <SCFDivider sx={{ mb: 4 }} />
      <SCFButton
        id="basic-button"
        aria-controls={openBasicMenu ? "basic-menu" : undefined}
        aria-haspopup="true"
        aria-expanded={openBasicMenu ? "true" : undefined}
        onClick={handleClickBasicMenu}
      >
        Open Basic Menu
      </SCFButton>
      <SCFBasicMenu
        id="basic-menu"
        anchorEl={anchorEl}
        open={openBasicMenu}
        onClose={handleCloseBasicMenu}
        MenuListProps={{
          "aria-labelledby": "basic-button",
        }}
      >
        <MenuItem onClick={handleCloseBasicMenu}>Text Field 1</MenuItem>
        <MenuItem onClick={handleCloseBasicMenu}>Text Field 2</MenuItem>
        <MenuItem onClick={handleCloseBasicMenu}>Text Field 3</MenuItem>
      </SCFBasicMenu>
    </>
  );
};

export default SCFBasicMenuDemo;