import React, { useState } from "react";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";

import { SCFChangePasswordForm, SCFDivider } from "../../components";
import Button from "@mui/material/Button";

const SCFChangePasswordFormDemo = () => {
  const validationMessages = {
    required: "Password is required",
    min: "Password must have at least 8 characters",
    max: "Password can't be longer than 64 characters",
    pattern: "The password must contains at least one uppercase one lowercase one number and one special case character",
  };

  const [texts, setTexts] = useState(validationMessages);

  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Change Password Form
      </Typography>
      <SCFDivider />
      <Button
        onClick={() => {
          setTexts({
            required: "Password requerido",
            min: "La contraseña debe tener al menos 8 caracteres",
            max: "La contraseña puede tener máximo 64 caracteres",
            pattern: "La contraseña debe contener al menos una mayúscula, una minúscula, un número y un carácter especial",
          });
        }}
      >
        Español
      </Button>
      <Button
        onClick={() => {
          setTexts({
            required: "Password is required",
            min: "Password must have at least 8 characters",
            max: "Password can't be longer than 64 characters",
            pattern:
              "The password must contains at least one uppercase one lowercase one number and one special case character",
          });
        }}
      >
        English
      </Button>
      <Grid item>
        <SCFChangePasswordForm
          type="change"
          onSubmit={(data) => {
            alert(JSON.stringify(data));
          }}
          similarityRatio={0.8}
          newPasswordValidationMessages={texts}
        />
      </Grid>
    </>
  );
};

export default SCFChangePasswordFormDemo;
