import React, { useState } from "react";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";

import { SCFButton, SCFClickAwayListener, SCFDivider } from "../../components";

const SCFClickAwayListenerDemo = () => {
  const [openClickAway, setOpenClickAway] = useState(false);

  const handleClickClickAway = () => {
    setOpenClickAway((prev) => !prev);
  };

  const handleClickAway = () => {
    setOpenClickAway(false);
  };

  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Click Away Listener
      </Typography>
      <SCFDivider sx={{ mb: 4 }} />
      <SCFClickAwayListener onClickAway={handleClickAway}>
        <Box sx={{ position: "relative" }}>
          <SCFButton type="button" onClick={handleClickClickAway}>
            Open menu dropdown
          </SCFButton>
          {openClickAway ? (
            <Box>Click me, I will stay visible until you click outside.</Box>
          ) : null}
        </Box>
      </SCFClickAwayListener>
    </>
  );
};

export default SCFClickAwayListenerDemo;
