import React from "react";


export type SCFCustomIconProps = {
    show?: boolean;
    size?: number | string;
    color?: string;
    iconClassName?: string;
    style?: object;
    iconPath?: string;
    onClick?: ()=> void;
};
export const SCFCustomIcon = ({ 
    size = 15, 
    color = 'red', 
    iconClassName, 
    style, 
    iconPath, 
    onClick
}: SCFCustomIconProps) => {
    return (
        <svg className={iconClassName} style={style} viewBox='0 0 24 24' onClick={onClick}
            width={`${size}px`} height={`${size}px`}
            xmlns="http://www.w3.org/2000/svg"
            xmlnsXlink="http://www.w3.org/1999/xlink">
            <path fill={color} d={iconPath} />
        </svg>
    );
}; 