import React, { useState } from 'react';

import { SCFButton, SCFCustomMiniDrawer, SCFDivider } from '../../components';
import { arrayMenuData } from './CustomMiniDrawerData';

import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";




export const SCFCustomMiniDrawerDemo = () => {
  const [open, setOpen] = useState(false);
  const handleDrawer = () => {
    setOpen(!open)
  }
  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        CustomMiniDrawer
      </Typography>
      <SCFDivider sx={{ mb: 4 }} />
      <Grid container spacing={2} p={4} direction="column">
        <Grid item alignContent={"center"}>
          <SCFButton onClick={handleDrawer}>
            Open menu
          </SCFButton>
         <SCFCustomMiniDrawer 
            arrayMenu={arrayMenuData}
            handleOpen={handleDrawer}
            handleRoutes = {()=> alert('rute')}
            handleSubOpen={handleDrawer}
            show={true}
            open= {open}
            />
        </Grid>
      </Grid>
    </>
  );
}
export default SCFCustomMiniDrawerDemo;

