import React, { useState } from "react";
import Typography from "@mui/material/Typography";

import { SCFButton, SCFDialog, SCFDivider } from "../../components";

const SCFDialogDemo = () => {

  const [openDialog, setOpenDialog] = useState(false);

  const handleClickOpenDialog = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Dialog
      </Typography>
      <SCFDivider sx={{ mb: 4 }} />

      <SCFButton variant="outlined" onClick={handleClickOpenDialog}>
        Open alert dialog
      </SCFButton>
      <SCFDialog
        open={openDialog}
        onClose={handleCloseDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
        title={<div className="titulo-modal">This is the Dialog Title</div>}
        content={
          <div>
            This is a Dialog component on the Foundations App, check the actions
          </div>
        }
        buttons={[
          { title: "cancel", onClick: handleCloseDialog },
          {
            title: "TEST",
            onClick: undefined,
            variant: "contained",
          },
        ]}
      ></SCFDialog>
    </>
  );
};

export default SCFDialogDemo;
