import React, { useState } from "react";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";

import { SCFPortal, SCFDivider } from "../../components";

const SCFPortalDemo = () => {
  
  const [showPortal, setShowPortal] = useState(false);
  const container = React.useRef(null);

  const handleClickPortal = () => {
    setShowPortal(!showPortal);
  };

  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Portal
      </Typography>
      <SCFDivider />
      <Grid item>
        <button type="button" onClick={handleClickPortal}>
          {showPortal ? "Unmount children" : "Mount children"}
        </button>
        <Box sx={{ p: 1, my: 1, border: "1px solid" }}>
          It looks like I will render here.
          {showPortal ? (
            <SCFPortal container={container.current}>
              <span>But I actually render here!</span>
            </SCFPortal>
          ) : null}
        </Box>
        <Box sx={{ p: 1, my: 1, border: "1px solid" }} ref={container} />
      </Grid>
    </>
  );
};

export default SCFPortalDemo;
