import React, { useState } from "react";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";

import { SCFSelect, SCFDivider } from "../../components";

const SCFSelectDemo = () => {

  const [selectValue, setSelectValue] = useState();
  const [filledSelectValue, setFilledSelectValue] = useState();

  const selectOptions = [
    { name: "Cat", value: "C" },
    { name: "Dog", value: "D" },
    { name: "Bird", value: "B" },
  ];

  const handleChange = (event: any) => {
    console.warn(event);
    setSelectValue(event.target.value);
  };

  const handleFilledSelectChange = (event: any) => {
    console.warn(event);
    setFilledSelectValue(event.target.value);
  };

  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Select
      </Typography>
      <SCFDivider sx={{ mb: 4 }} />
      <Grid container spacing={2}>
        <Grid item alignContent={"left"}>
          <SCFSelect
            id="demo-simple-select-outlined"
            name="Pet"
            value={selectValue}
            sx={{ width: "300px" }}
            variant="outlined"
            required={false}
            onChange={handleChange}
            options={selectOptions}
          ></SCFSelect>
        </Grid>
        <Grid item alignContent={"right"}>
          <SCFSelect
            id="demo-simple-select-filled"
            label="Choose one"
            name="Pet"
            value={filledSelectValue}
            sx={{ width: "300px" }}
            variant="filled"
            required={false}
            onChange={handleFilledSelectChange}
            options={selectOptions}
          ></SCFSelect>
        </Grid>
      </Grid>
    </>
  );
};

export default SCFSelectDemo;
