import React, { useState } from "react";
import Typography from "@mui/material/Typography";

import { SCFDivider } from "../../components";
import SCFSidebar from "../../components/SCFSidebar/SCFSidebar";
import { IconButton } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";

const SCFSliderDemo = () => {
  const [open, setOpen] = useState(false);
  const links = [
    {to: '/pa-statement', icon: 'fas fa-hand-holding-usd',text: 'test Icon'},
    {
      to: '/orders-search',
      icon: 'fas fa-shopping-cart',
      text: 'prueba',
    }
  ];
  const handleSidebarOpen = () =>{
    setOpen(!open);
  }
  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Sidebar
      </Typography>
      <SCFDivider />
      <IconButton color="inherit" aria-label="open drawer" onClick={() => handleSidebarOpen()} edge="start" className="buttonMenu">
        <MenuIcon style={{ fontSize: 30 }} />
      </IconButton>
      <SCFSidebar
        show
        open={open}
        links={links} />
    </>
  );
};

export default SCFSliderDemo;
