import React, { useState } from "react";
import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";

import { SCFSnackbar, SCFDivider, SCFButton } from "../../components";

const SCFSnackbarDemo = () => {
    
  const [open, setOpen] = useState(false);

  // Snackbar
  const handleClicSnackbar = () => {
    setOpen(true);
  };
  const handleCloseSnackbar = () => {
    setOpen(false);
  };

  return (
    <>
      <Typography variant="h1" gutterBottom fontWeight={"bold"} sx={{ mt: 8 }}>
        Snackbar
      </Typography>
      <SCFDivider />
      <Grid item>
        <SCFButton onClick={handleClicSnackbar}>Open simple snackbar</SCFButton>
        <SCFSnackbar
          open={open}
          autoHideDuration={6000}
          onClose={handleCloseSnackbar}
          message="Note archived"
        />
      </Grid>
    </>
  );
};

export default SCFSnackbarDemo;
